import React, { useState, useEffect } from 'react'
import { Button, Image, StyleSheet, Text, TouchableOpacity, View } from 'react-native'

import OptionButton from './OptionButton';
import QuestionArea from './QuestionArea';

import images from '../../values/images';
import { getRandomQuestion, useRandomQuestion } from '../hooks/databaseHooks';
import questionsList from '../models/questionsList';
import { inject, observer } from 'mobx-react';

var nextQuestion = false;

const QuizApp = inject('quizStore')(observer((props) => {
    const quizStore = props.quizStore;

    return (
        <View style={styles.container}>
            <QuestionArea/>
            <View  style={styles.optionsContainer}>
                {quizStore.currentQuestion.options.map((item, i) => (
                    <OptionButton key={i} apple={item} onClick={quizStore.changeSelection}
                        selected={quizStore.isSelected(item)}/>
                ))}
            </View>
            <View style={styles.submitContainer}>
                <TouchableOpacity style={styles.submitButton} onPress={() => quizStore.submitAnswer()}>
                <Image
                    style={styles.submitButtonBackground}
                    source={images.submitButton}
                    resizeMode='contain'/>
                <Text style={styles.submitText}> SUBMIT </Text>
                </TouchableOpacity>
            </View>
        </View>
    )
}))

export default QuizApp

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundImage: src=('https://i.pinimg.com/originals/3d/e0/7f/3de07f0da80b7070a0555ba04882339e.png'),
        alignItems: 'center'
      },
      optionsContainer: {
        width: '100%',
        alignItems: 'center'
      },
      submitContainer: {
        marginVertical: 15,
        width: '100%',
        alignItems: 'center',
        justifyContent: 'center'
      },
      submitButtonBackground: {
        width: 214,
        height: 50,
        position: 'absolute',
        zIndex: 1
      },
      submitButton: {
        width: '85%',
        height: 50,
        alignItems: 'center',
        justifyContent: 'center'
      },
      submitText: {
        color: 'white',
        fontSize: 35,
        fontFamily: 'PokemonPixelFont',
        zIndex: 2
      }
})
